        </form>
      </div>

      <div className="stats">
        <span>üìä T·ªïng c·ªông: {total} nh√¢n vi√™n</span>
        <span>üìÑ Trang {page} / {totalPages}</span>
      </div>

      <div className="table-container">
        <table className="employee-table">
          <thead>
            <tr>
              <th>M√£ NV</th>
              <th>H·ªç t√™n</th>
              <th>Email</th>
              <th>SƒêT</th>
              <th>Gi·ªõi t√≠nh</th>
              <th>Ng√†y sinh</th>
              <th>Ch·ª©c v·ª•</th>
              <th>Tr√¨nh ƒë·ªô</th>
              <th>Thao t√°c</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee.id}>
                <td>{employee.manv}</td>
                <td>{employee.tennv}</td>
                <td>{employee.email}</td>
                <td>{employee.sdt}</td>
                <td>{employee.gtinh}</td>
                <td>{new Date(employee.ngsinh).toLocaleDateString('vi-VN')}</td>
                <td>{employee.macv}</td>
                <td>{employee.trinhdo}</td>
                <td>
                  <button 
                    className="edit-button"
